import { useState } from "react";
import EduProgram from "./pages/EduProgram";
import { saveToFile, loadFromFile } from "./utils/fileUtils";

export default function App() {
  const [data, setData] = useState(null);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Образовательная программа</h1>
      <div className="flex gap-2 mb-4">
        <button onClick={() => saveToFile(data)} className="p-2 bg-blue-500 text-white rounded">Сохранить</button>
        <input type="file" onChange={(e) => loadFromFile(e, setData)} className="p-2 border rounded" />
      </div>
      <EduProgram data={data} setData={setData} />
    </div>
  );
}
