// src/components/Card.js
import React, { useState, useCallback } from 'react';
import { CardContainer, CardText, CardDescription, DeleteCardButton } from './Card.styles';
import styled from 'styled-components';

const CheckboxContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 5px;

  label {
    margin-left: 5px;
    font-size: 0.8em;
    color: #777;
  }
`;

const ArrowButton = styled.button`
  background-color: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  margin: 0 5px;
  font-size: 1.2em;
  color: #888;

  &:hover {
    color: #555;
  }
`;

function Card({ card, listId, onTextChange, onDescriptionChange, onDeleteCard, setSelectedCard, data, moveCard }) {
  const [text, setText] = useState(card.text);
  const [description, setDescription] = useState(card.description);
  const [isEditingText, setIsEditingText] = useState(false);
  const [isEditingDescription, setIsEditingDescription] = useState(false);
  const [isChecked, setIsChecked] = useState(false);

  const handleTextChange = useCallback((e) => {
    setText(e.target.value);
  }, []);

  const handleDescriptionChange = useCallback((e) => {
    setDescription(e.target.value);
  }, []);

  const handleTextBlur = useCallback(() => {
    setIsEditingText(false);
    onTextChange(listId, card.id, text);
  }, [card.id, listId, text, onTextChange]);

  const handleDescriptionBlur = useCallback(() => {
    setIsEditingDescription(false);
    onDescriptionChange(listId, card.id, description);
  }, [card.id, listId, description, onDescriptionChange]);

  const handleTextClick = useCallback(() => {
    setIsEditingText(true);
  }, []);

  const handleDescriptionClick = useCallback(() => {
    setIsEditingDescription(true);
  }, []);

  const handleCheckboxChange = useCallback((e) => {
    const checked = e.target.checked;
    setIsChecked(checked);
    setSelectedCard(checked ? card : null);
  }, [card, setSelectedCard]);

  return (
    <CardContainer>
      <CheckboxContainer>
        <input
          type="checkbox"
          checked={isChecked}
          onChange={handleCheckboxChange}
        />
        <label>Изменить метки</label>
      </CheckboxContainer>
      {isEditingText ? (
        <input
          type="text"
          value={text}
          onChange={handleTextChange}
          onBlur={handleTextBlur}
          autoFocus
        />
      ) : (
        <CardText onClick={handleTextClick}>
          {text}
        </CardText>
      )}
      {isEditingDescription ? (
        <CardDescription
          value={description}
          onChange={handleDescriptionChange}
          onBlur={handleDescriptionBlur}
        />
      ) : (
        <CardText onClick={handleDescriptionClick}>
          {description}
        </CardText>
      )}
          <ArrowButton onClick={() => moveCard(card.id, listId, card.id, listId, 'up')}>&#9650;</ArrowButton>
          <ArrowButton onClick={() => moveCard(card.id, listId, card.id, listId, 'down')}>&#9660;</ArrowButton>
      <DeleteCardButton onClick={() => onDeleteCard(listId, card.id)}>Удалить карточку</DeleteCardButton>
    </CardContainer>
  );
}

export default Card;
