// src/components/Card.js
import React, { useState, useCallback } from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  background-color: white;
  border-radius: 3px;
  padding: 8px;
  margin-bottom: 8px;
  box-shadow: 0 1px 0 rgba(9,30,66,.25);
`;

const CardTextarea = styled.textarea`
    resize: none;
    width: 100%;
    border: none;
    overflow: hidden;
    outline: none;
    font-size: 1em;
    padding: 5px;
    box-sizing: border-box;
    background-color: transparent;
    &:focus {
        background-color: #eee;
    }
`;

const CardDescriptionTextarea = styled.textarea`
    resize: vertical;
    width: 100%;
    border: 1px solid #ccc;
    border-radius: 3px;
    padding: 5px;
    margin-top: 5px;
    box-sizing: border-box;
    font-size: 0.9em;
    outline: none;
    &:focus {
        border-color: #0079bf;
    }
`;

const DeleteCardButton = styled.button`
    background-color: #e74c3c;
    color: white;
    border: none;
    padding: 5px 10px;
    border-radius: 3px;
    cursor: pointer;
    margin-top: 5px;
`;

function Card({ card, listId, onTextChange, onDescriptionChange, onDeleteCard }) {
    const [text, setText] = useState(card.text);
    const [description, setDescription] = useState(card.description);

    const handleTextChange = useCallback((e) => {
        setText(e.target.value);
        onTextChange(listId, card.id, e.target.value);
    }, [listId, card.id, onTextChange]);

    const handleDescriptionChange = useCallback((e) => {
        setDescription(e.target.value);
        onDescriptionChange(listId, card.id, e.target.value);
    }, [listId, card.id, onDescriptionChange]);

    return (
        <CardContainer>
            <CardTextarea
                value={text}
                onChange={handleTextChange}
                placeholder="Введите текст предмета..."
            />
            <CardDescriptionTextarea
                value={description}
                onChange={handleDescriptionChange}
                placeholder="Введите описание..."
            />
            <DeleteCardButton onClick={() => onDeleteCard(listId, card.id)}>Удалить карточку</DeleteCardButton>
        </CardContainer>
    );
}

export default Card;
