// src/App.styles.js
import styled from 'styled-components';

export const AppContainer = styled.div`
  display: flex;
  overflow-x: auto;
  min-height: 100vh;
  background-color: #f4f5f7;
  padding: 20px;
`;

export const AddButton = styled.button`
  background-color: #5aac44;
  color: white;
  border: none;
  border-radius: 3px;
  padding: 8px 12px;
  margin-left: 10px;
  cursor: pointer;
`;

export const FileInput = styled.input`
  margin-left: 10px;
  display: none;
`;

export const ButtonContainer = styled.div`
  display: flex;
  align-items: center;
  margin-top: 10px;
`;

export const LoadButton = styled.label`
  background-color: #2e86de;
  color: white;
  border: none;
  border-radius: 3px;
  padding: 8px 12px;
  margin-left: 10px;
  cursor: pointer;
  display: inline-block;
`;
