import { v4 as uuidv4 } from 'uuid';

export const handleAddCard = (listId, data, setData) => {
  const newCard = {
    id: uuidv4(),
    text: 'Новая карточка',
    description: 'Описание', // <--- Добавили description
  };

  const newData = {
    ...data,
    lists: data.lists.map(list =>
      list.id === listId ? { ...list, cards: [...list.cards, newCard] } : list
    ),
  };

  setData(newData);
};

export const handleDeleteCard = (listId, cardId, data, setData) => {
  const newData = {
    ...data,
    lists: data.lists.map(list => ({
      ...list,
      cards: list.cards.filter(card => card.id !== cardId),
    })),
  };
  setData(newData);
};

export const handleCardTextChange = (listId, cardId, newText, data, setData) => {
  const newData = {
    ...data,
    lists: data.lists.map(list => ({
      ...list,
      cards: list.cards.map(card =>
        card.id === cardId ? { ...card, text: newText } : card
      ),
    })),
  };
  setData(newData);
};

export const handleCardDescriptionChange = (listId, cardId, newDescription, data, setData) => {
  const newData = {
    ...data,
    lists: data.lists.map(list => ({
      ...list,
      cards: list.cards.map(card =>
        card.id === cardId ? { ...card, description: newDescription } : card
      ),
    })),
  };
  setData(newData);
};
